module.exports = function(puremvc, facade, config) {
	// include class
	puremvc.include("IndexRouteMediator",__dirname+"/src/view");

	// init class and register to facade
	var indexRouteMediator = new npmvc.view.mediator.IndexRouteMediator();
	indexRouteMediator.init(config);
	facade.registerMediator(indexRouteMediator);

}