module.exports = function(include, puremvc) {
	/**
	 * @class my.custom.Route
	 * @extends npmvc.view.route.Route
	 *
	 */
	return puremvc.define(
		// CLASS INFO
		{
			name: "my.custom.IndexRoute",
			parent: npmvc.view.route.Route
		},
		// INSTANCE MEMBERS 
		{
			setRoute:function(){
				this.router.get("/", this.indexRoute.bind(this));
			},
			indexRoute: function(req, res) {
				res.send("indexRoute");
			}
		},
		// STATIC MEMBERS	
		{
			NAME: "my.custom.IndexRoute"
		});
}