module.exports = function(include, puremvc) {
	/**
	 * @class my.custom.Route
	 * @extends npmvc.view.route.Route
	 *
	 */
	return puremvc.define(
		// CLASS INFO
		{
			name: "my.custom.SomeRoute",
			parent: npmvc.view.route.Route
		},
		// INSTANCE MEMBERS 
		{
			setRoute:function(router){
				this.counter = 0;
				router.get("/someroute", this.routeHandler.bind(this));
			},
			routeHandler: function(req, res) {
				res.render("someroute",{});
			}
		},
		// STATIC MEMBERS	
		{
			NAME: "my.custom.SomeRoute"
		});
}